<main><section class="page"><div class="container"><nav aria-label="breadcrumb"><ol class="breadcrumb"><li class="breadcrumb-item"><a href="<?php echo base_url(""); ?>">Beranda</a></li><li class="breadcrumb-item active">Menu</li></ol></nav><div class="page-content"><div class="filter"><form method="GET"><div class="row"><div class="col-lg-3 col-8"><div class="form-group row"><label for="kategori" class="col-2 col-form-label">Filter</label><div class="col-10"><select name="kategori" id="kategori" class="form-control"><option value="">Semua Kategori</option><?php foreach ($kategori as $key => $value): ?><option <?php if ($this->input->get('kategori')==$value['url_kategori'] || $this->uri->segment(2)==$value['url_kategori']) {echo "selected";}?> value="<?php echo $value['url_kategori']; ?>"><?php echo $value['nama_kategori']; ?></option><?php endforeach ?></select></div></div></div><div class="col-lg-4 col-4 text-left"><button type="submit" class="btn btn-secondary">Cari</button><?php if (!empty($this->input->get('kategori'))): ?><a href="<?php echo base_url('menu') ?>" class="btn btn-secondary">Reset</a><?php endif ?></div></div></form></div><div class="kartu-wrapper"><ul class="kartu__list row"><?php foreach ($produk as $key => $value): ?><li class="col-lg-3 col-md-4 col-sm-6 col-12"><div class="kartu__list-item"><a href="<?php echo base_url("/menu/detail/".$value['url_produk']); ?>"><img class="img-fluid kartu__list-item-image" src="<?php echo base_url("assets/img/produk/".$value['foto'][0]['nama_produk']); ?>" alt="<?php echo $value['judul']; ?>" /></a><div class="kartu__list-item-info"><div style="min-height: 40px;"><h3><a href="<?php echo base_url("/menu/detail/".$value['url_produk']); ?>"><?php echo $value['judul']; ?></a></h3></div><span><?php echo $value['min_required']; ?></span></div></div></li><?php endforeach ?><?php if (empty($produk)): ?><li class="col-lg-3 col-md-4 col-sm-6 col-12"><p>Produk tidak ada pada kategori ini.</p></li><?php endif ?></ul></div></div></div></section></main>